public class ManterTelefonesAction extends CrudAction {
	public void setTelefoneEndereco(TelefoneEnderecoService telefoneEnderecoService) {
		this.defaultService = telefoneEnderecoService;
	}
    public void removeById(java.lang.Long id) {
        ((TelefoneEnderecoService)defaultService).removeById(id);
    }

	/**
	 *
	 */
	@ParametrizedAttribute(type = java.lang.Long.class)
    public void removeByIds(List ids) {
    	((TelefoneEnderecoService)defaultService).removeByIds(ids);
    }

    public TelefoneEndereco findById(java.lang.Long id) {
    	TelefoneEndereco te = ((TelefoneEnderecoService)defaultService).findById(id);
    	
    	// Formatação do nrIdentificação da pessoa
    	te.getPessoa().setNrIdentificacao(
    			FormatUtils.formatIdentificacao(
    					te.getPessoa().getTpIdentificacao().getValue(), 
    					te.getPessoa().getNrIdentificacao()));
    	
    	return te;
    }
    
    public Serializable store(TelefoneEndereco bean) {
    	if (bean.getEnderecoPessoa() != null){
    		if (bean.getEnderecoPessoa().getIdEnderecoPessoa() == null){
    			bean.setEnderecoPessoa(null);
    		}
    	}
    	
    	return ((TelefoneEnderecoService)defaultService).store(bean);
    }

}
