public class EmitirConhecimentosCanceladosAction extends ReportActionSupport {
	private MotivoCancelamentoService motivoCancelamentoService;
	private FilialService filialService;

	@Override
	public java.io.File execute(TypedFlatMap parameters) throws Exception {
		return super.execute(parameters);
	}

	public List findMotivoCancelamento() {
		return motivoCancelamentoService.findAtivosLookup();
	}

	public List montaLookupFilial(TypedFlatMap criteria) {
		return filialService.findLookupBySgFilial(criteria.getString("sgFilial"), criteria.getString("tpAcesso"));
	}

	public Map getBasicData() {
		Filial filial = SessionUtils.getFilialSessao();
		boolean isMatriz = SessionUtils.isFilialSessaoMatriz();
		TypedFlatMap dadosUsuario = new TypedFlatMap();

		dadosUsuario.put("dataInicial", JTDateTimeUtils.convertFrameworkDateToFormat(JTDateTimeUtils.getDataAtual().toString(),"dd/MM/yyyy"));
		dadosUsuario.put("dataFinal", 	JTDateTimeUtils.convertFrameworkDateToFormat(JTDateTimeUtils.getDataAtual().toString(),"dd/MM/yyyy"));

		if(!isMatriz) {
			TypedFlatMap fil = new TypedFlatMap();
			fil.put("idFilial", filial.getIdFilial());
			fil.put("sgFilial", filial.getSgFilial());
			dadosUsuario.put("filial", fil);

			TypedFlatMap pessoa = new TypedFlatMap();
			pessoa.put("nmFantasia", filial.getPessoa().getNmFantasia());
			fil.put("pessoa", pessoa);
		}
		return dadosUsuario;
	}

	public void setConhecimentoCanceladoService(ConhecimentoCanceladoService conhecimentoCanceladoService) {
		this.reportServiceSupport = conhecimentoCanceladoService;
	}
	public void setMotivoCancelamentoService(MotivoCancelamentoService motivoCancelamentoService) {
		this.motivoCancelamentoService = motivoCancelamentoService;
	}
	public void setFilialService(FilialService filialService) {
		this.filialService = filialService;
	}
}